package loops;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(boolean a = true; a ==true ; a = false) {
			System.out.println(a);
		}
		
		System.out.println();
		for(boolean a = true; a ==false ; a = true) {
			System.out.println(a);
		}
		System.out.println();
		
		for(boolean a = false; false ; a = true) {
			System.out.println(a);
		}
		
		System.out.println();
		
		for(boolean a = false; true ; a = false) {
			System.out.println(a);
		}
		
       
	}

}
